function getCookie(e){for(var t=e+"=",o=document.cookie.split(";"),c=0;c<o.length;c++){for(var i=o[c];" "==i.charAt(0);)i=i.substring(1);if(0==i.indexOf(t))return i.substring(t.length,i.length)}return""}function createAcceptCookie(){var e=document.getElementById("cookie-banner");document.cookie="accepted_cookie_policy=yes; expires="+setExpiryDate()+";",e.classList.add("accepted-cookie-policy")}function setExpiryDate(){var e=new Date;return e.setFullYear(e.getFullYear()+1),e.toUTCString()}var isCookieSet=getCookie("accepted_cookie_policy");""===isCookieSet&&(document.getElementById("cookie-banner").classList.add("accepted"),document.getElementById("cookie-button").addEventListener("click",createAcceptCookie));